mapper.select:
SELECT * FROM tb_truck 
WHERE TRUCK_NUM IN 
( 
	SELECT DISTINCT TRUCK_NUM FROM tb_menu 
	WHERE
		
			${category} = #{eat}
		
			
				OR ${category} = #{drink}
			
	
			
				OR ${category} = #{enjoy}
			
		
	
		
			
				${category} = #{drink}
				
				
					OR ${category} = #{enjoy}
				
			
			
			
				
					${category} = #{enjoy}
				
			
		
)
